mapper.select:
	
	SELECT COUNT(NICK_NAME)
	FROM MEMBER
	WHERE  NICK_NAME=#{mNickName}


	SELECT USER_PWD
	FROM MEMBER
	WHERE USER_ID=#{mId}

mapper.update:

		UPDATE MEMBER
		SET USER_PWD=#{mPwd}
		WHERE USER_ID=#{mId}

mapper.select:
	
	SELECT COUNT(SOCIAL_ID)
	FROM MEMBER
	WHERE  SOCIAL_ID=#{socialId}
